fcf.module({name:"fcfControls:templates/language-selector.wrapper.js",dependencies:["fcf:NRender/Wrapper.js"],module:function(Wrapper){return class extends Wrapper{constructor(a_initializeOptions){super(a_initializeOptions)}onArgLanguage(a_value,a_suffix){let languageIdentification=fcf.getSystemVariable("fcf:languageIdentification"),languages=fcf.getSystemVariable("fcf:languages");fcf.getContext().set("language",a_value);let url=window.location.href;if(languageIdentification.byPrefix){let pathArr=fcf.trim(window.location.pathname,"/").split("/"),prefix=pathArr[0];if(2==prefix.length&&prefix in languages){let newPathArr=fcf.clone(pathArr);newPathArr[0]=a_value,url=url.replace("/"+pathArr.join("/"),"/"+newPathArr.join("/"))}}if(languageIdentification.byParameter){let urlInfo=fcf.parseUrl(url);urlInfo.args[languageIdentification.parameter]=a_value,url=fcf.buildUrl(urlInfo.referer,urlInfo.args)}window.location.href=url}}}});